import React, {useState, useCallback, useEffect } from 'react';
import ReactDOM from 'react-dom';
import Dropdown  from 'react-dropdown';
import { csv, scaleLinear, extent, min, max, format } from 'd3';
import { useData } from './useData';
import { AxisBottom } from './AxisBottom';
import { AxisLeft } from './AxisLeft';
import { Marks } from './Marks';
import { ownDropdown } from './ownDropdown';
import './style.css';

const width = 960;
const menuHeight = 50;
const height = 600 - menuHeight;
const margin = { top: 20, right: 20, bottom: 70, left: 60 };
const xAxisLabelOffset = 40;
const AxisOffset = 5;
const circleRadius = 5;

console.log(Dropdown)

const attributes = [
    { value: 'sepal_length', label: 'Sepal Length' },
    { value: 'sepal_width', label: 'Sepal Width' },
    { value: 'petal_length', label: 'Petal Length' },
    { value: 'petal_width', label: 'Petal Width' },
    { value: 'species', label: 'Species' }
];

const getValue = value => {
    for( let i = 0; i < attributes.length; i++) {
        if(attributes[i].value === value) {
            return attributes[i].label;
        }
    }
};

const App = () => {
    const data = useData();

    const initialXAttribute = 'petal_length'
    const [xAttribute, setXAttribute] = useState(initialXAttribute);
    const xValue = d => d[xAttribute];
    const xAxisLabel = getValue(xAttribute);

    const initialYAttribute = 'petal_width'
    const [yAttribute, setYAttribute] = useState(initialYAttribute);
    const yValue = d => d[yAttribute];
    const yAxisLabel = getValue(yAttribute);

    if (!data) {
        return <div>Loading...</div>;
    }

    const innerHeight = height - margin.top - margin.bottom;
    const innerWidth = width - margin.left - margin.right;





    const siFormat = format('.2s');
    const xAxisTickFormat = tickValue => siFormat(tickValue).replace('G', 'B');

    const xScale = scaleLinear()
        .domain(extent(data, xValue))
        .range([0, innerWidth])
        .nice();

    const yScale = scaleLinear()
        .domain(extent(data, yValue))
        .range([innerHeight - 10, 0 ])
        .nice();


    return(
        <container className="plot">
            <label for="x-select">Choose the x-Axis:</label>
            <Dropdown 
                options={attributes}
                id="x-select"
                selectedValue={xAttribute}
                onSelectedValueChange={setXAttribute}
            />
            <label for="y-select">Choose the y-Axis:</label>
            <Dropdown 
                options={attributes}
                id="y-select"
                selectedValue={yAttribute}
                onSelectedValueChange={setYAttribute}
            />
            <h1 className="header">Iris Dataset</h1>
            <svg className="plot" width={width} height={height}>
                <g transform={`translate(${margin.left}, ${margin.top})`}>
                    <AxisBottom 
                        xScale={xScale} 
                        innerHeight={innerHeight} 
                        tickFormat={xAxisTickFormat}
                        tickOffset={AxisOffset}
                        />
                    <text
                        className="axis-label"
                        textAnchor="middle"
                        transform={'rotate(-90)'}
                        dy= {-xAxisLabelOffset}
                        dx={-innerHeight / 2}
                    >
                        {yAxisLabel}
                    </text>                
                    <AxisLeft 
                        yScale={yScale} 
                        innerWidth={innerWidth} 
                        tickOffset={AxisOffset}
                    />
                    <text
                        className="axis-label"
                        x={innerWidth / 2}
                        y={innerHeight + xAxisLabelOffset}
                        textAnchor="middle"
                    >
                        {xAxisLabel}
                    </text>
                    <Marks 
                        data={data} 
                        xScale={xScale} 
                        yScale={yScale}
                        xValue={xValue}
                        yValue={yValue}
                        tooltipFormat={xAxisTickFormat}
                        circleRadius={circleRadius}
                        />
                </g>
            </svg>
        </container>
        );
    };
const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);
