export const ColorLegend = ({ 
    colorScale, 
    circleRadius, 
    tickSpacing = 20,
    tickTextOffset = 20 
}) => 
    colorScale.domain().map((domainValue, i) => (
        <g transform={`translate(0, ${i * tickSpacing})`}>
            <circle fill={colorScale(domainValue)} r={circleRadius} />
            <text x={tickTextOffset = 20} dy=".32em" >{domainValue}</text>
        </g>
    ));
