export const Marks = ({ 
    data,
    xScale,
    yScale,
    xValue,
    yValue,
    tooltipFormat,
    circleRadius
}) => 
    data.map(d => (
        <circle
            className='mark'
            cx = {xScale(xValue(d))}
            cy = {yScale(yValue(d))}
            r = {circleRadius}
            key = {d.id}
        >
            <title>{tooltipFormat(xValue(d), yValue(d))}</title>
        </circle>        
    ));
