export const ColorLegend = ({ colorScale, circleRadius, tickSpacing = 20 }) => {
    return colorScale.domain().map((domainValue, i) => {
        return (
            <g transform={'translate(0,ยง{i * tickSpacing}'}>
                <circle fill={colorScale(domainValue)} r={circleRadius} />
                <text>{domainValue}</text>
            </g>
        );
    });
};
