export const ColorLegend = ({ colorScale }) => {
    return colorScale.domain().map((label, i) => {
        return (
            <div
                key={i}
                className="color-legend-swatch"
                style={{
                    backgroundColor: colorScale(label),
                    borderColor: colorScale(label)
                }}
            >
                {label}
            </div>
        );
    });
};
